mapper.select:
SELECT * FROM dictionary
WHERE 1=1
AND id = #{id}
AND LOWER(value) LIKE CONCAT('%', LOWER(#{value}), '%')
AND is_active = #{isActive}
LIMIT #{limit}

mapper.update:
UPDATE dictionary
SET category = #{category}, value = #{value}, description = #{description}, is_active = #{isActive}
WHERE id = #{id}

UPDATE dictionary
SET is_active = false, deleted_at = #{timestamp}
WHERE id = #{id}

UPDATE dictionary
SET is_active = true, deleted_at = null
WHERE id = #{id}
