mapper.select:
SELECT c.id AS c_id, c.name AS c_name, c.description AS c_description, c.is_active AS c_is_active, c.created_at AS c_created_at, c.updated_at AS c_updated_at,
i.id AS i_id, i.value AS i_value, i.description AS i_description, i.is_active AS i_is_active, i.created_at AS i_created_at, i.updated_at AS i_updated_at, i.category_id AS i_category_id
FROM ( SELECT * FROM categories WHERE is_active != false LIMIT #{limit} ) c LEFT JOIN items i ON c.id = i.category_id ORDER BY c.id ASC, i.id ASC;
