mapper.select:
SELECT *
FROM dictionary


        id = #{id}


        AND LOWER(value) LIKE CONCAT('%', LOWER(#{value}), '%')


        AND is_active = #{is_active}


ORDER BY id ASC

    LIMIT #{limit}

mapper.update:
UPDATE dictionary
SET value = #{value}, description = #{description}, is_active = #{isActive}, deleted_at = #{deletedAt}
WHERE id = #{id}

UPDATE dictionary
SET is_active = false, deleted_at = NOW()
WHERE id = #{id}

UPDATE dictionary
SET is_active = true, deleted_at = NULL
WHERE id = #{id}

mapper.select:
SELECT is_active
FROM dictionary
WHERE id = #{id}
