mapper.select:
SELECT id, value, description, is_active, deleted_at
FROM dictionary
WHERE

    id = #{id}


    AND LOWER(value) LIKE CONCAT('%', LOWER(#{value}), '%')


    AND is_active = #{is_active}

ORDER BY id ASC
LIMIT #{limit}

mapper.update:
UPDATE dictionary
SET value = #{value},
description = #{description},
is_active = #{is_active},
deleted_at = #{deleted_at}
WHERE id = #{id}

UPDATE dictionary
SET is_active = true, deleted_at = NULL
WHERE id = #{id}

mapper.select:
SELECT is_active
FROM dictionary
WHERE id = #{id}
