mapper.update:
UPDATE dictionary
SET value = #{value},
    description = #{description},
    category_id = #{categoryId}
WHERE id = #{id}

mapper.select:
SELECT * FROM dictionary


        AND id ILIKE CONCAT('%', #{id}, '%')


        AND value ILIKE CONCAT('%', #{value}, '%')


        AND is_active = #{isActive}


ORDER BY id

    LIMIT #{limit}
