mapper.update:
UPDATE dictionaries
SET value = #{value},
    description = #{description},
    category_id = #{categoryId}
WHERE id = #{id}

mapper.select:
SELECT id, value, description, is_active, deleted_at, category_id FROM dictionaries
where 1=1

    AND value = #{req.value}


    AND is_active = #{req.isActive}

ORDER BY value

    offset #{req.offset}


    LIMIT #{req.limit}
